import config from "../config";
import { Game, Point, Sprite } from "../engine";
import { SpawnParticles } from "../engine/components/Particles";
import { lerp, random, Vector2 } from "../engine/utils/math";

export type OreType = "stone" | "cracked-stone" | "cidium";
enum OreHp {
    "stone" = 10,
    "cracked-stone" = 6,
    "cidium" = 20,
}

// export class Ore extends Point {
export class Ore extends Sprite {
    type: OreType

    hp: number

    elapsedBeforeFall: number
    allowFall: boolean
    willFall: boolean
    animatedScale: number

    constructor(type: OreType, position: Vector2) {
        super(`ore-${ type }`, type, {
        // super(`ore-${ type }`, {
            position: position.mul(config.SPRITE_PIXEL_SIZE * config.SPRITE_SCALE),
            colliderType: "solid"
        });
        this.type = type;
        
        this.hp = OreHp[type];

        this.elapsedBeforeFall = 0;
        this.allowFall = false;
        this.willFall = false;
        this.animatedScale = 1;
    }

    init(game: Game) {
        super.init(game);

        this.rotation = Math.floor(random(1, 5)) * Math.PI;
        if (this.type == "cracked-stone") {
            this.collider.type = "solid-dynamic";
            this.offset.y = -1;
        }
    }

    update() {
        super.update();
        
        this.animatedScale = lerp(this.animatedScale, 1, .2);
        this.scale.set(this.animatedScale, this.animatedScale);

        if (this.type == "cracked-stone") {
            this.velocity.y *= .9;

            if (!this.collider.collidesWith?.bottom) {
                if (!this.willFall) {
                    SpawnParticles(this.game, this.position.add(new Vector2(0, 40)), {
                        velocity: ()=> new Vector2(random(-2, 2), 0),
                        box: ()=> new Vector2(random(-40, 40), 0),
                        count: 5,
                    });
                    this.willFall = true;
                }
                if (!this.allowFall) {
                    if ((this.game.clock.elapsed - this.elapsedBeforeFall) % config.ORE_FALL_DELAY == 0)
                        this.allowFall = true;
                }
                if (this.allowFall)
                    this.velocity.y ++;
            } else {
                this.allowFall = false;
                this.willFall = false;
                this.elapsedBeforeFall = this.game.clock.elapsed
            }
            this.collider.collidable = this.collider.collidesWith?.bottom;

            this.collider.collidesWith = null;

        }

        // console.log(this.collider.collidesWith?.id);
    }
    render() {
        super.render();
        // this.darken();
    }

    darken() {

        // const player = this.game.getChildById("player");

        // const d = player.position.distance(this.position) - 150;

        // this.game.renderer.drawRect(
        //     `rgba(0, 0, 0, ${ d > 100 ? 1 : d / 100 })`,
        //     1.01, 1.01,
        //     this.position, 0
        // );
        // this.game.renderer.context.shadowOffsetX = this.position.x - 96 / 2;
        // this.game.renderer.context.shadowOffsetY = this.position.y - 96 / 2;
        // this.game.renderer.context.shadowBlur = 15;
        // this.game.renderer.context.shadowColor = `rgba(0, 0, 0, ${ d > 200 ? 1 : d / 200 })`;

    }
    hit(damage: number) {

        this.hp -= damage;
        this.animatedScale = .8;

        if (this.hp <= 0) {
            SpawnParticles(this.game, this.position, {
                colors: this.type == "cidium" ? [config.BLACK, config.YELLOW] : [config.BLACK],
            });
            this.game.removeChildById(this.id);
        }

    }
}
