import { Game, Group } from "../engine";
import { Vector2 } from "../engine/utils/math";
import { Ore } from "../objects/ores/Ore";
import { seed, perlin2, simplex2 } from "../engine/utils/noise";
import { Config } from "../config";
import { Destrony } from "../objects/ores/Destrony";
import { Cidium } from "../objects/ores/Cidium";
import { DeepStone } from "../objects/ores/DeepStone";
import { Stone } from "../objects/ores/Stone";
import { CrackedStone } from "../objects/ores/CrackedStone";

// export type Chunk = {
//     id: string
//     pos: Vector2
// }
// type GeneratorSettings = {
//     value: [number, number]
//     height: [number, number]
//     ore: typeof Ore | any | null
//     divider?: number
// }
// type Generator = {
//     createChunk(game: Game, pos: Vector2, destroyedOres: string[]): Chunk | undefined
// }
let __chunkId = 0;

export function initGenerator(game: Game): Generator {

    return applyGenerator([
        // Stones
        {
            value: [0, 1],
            height: [0, 43],
            ore: Stone,
        },
        {
            value: [.5, .6],
            height: [5, Config.WORLD_HEIGHT],
            divider: 5,
            ore: CrackedStone
        },
        {
            value: [.8, 1],
            height: [30, 43],
            divider: 2,
            ore: DeepStone
        },
        {
            value: [0, 1],
            height: [42, Config.WORLD_HEIGHT],
            ore: DeepStone
        },
        
        // Ores
        {
            value: [.85, 1],
            height: [12, Config.WORLD_HEIGHT - 10],
            divider: 5,
            ore: Cidium
        },

        // Holes
        {
            value: [0, .5],
            height: [5, Config.WORLD_HEIGHT],
            ore: null,
        },
    ]);

}

function applyGenerator(generatorSettings: GeneratorSettings[]): Generator {
    const s = Date.now();
    seed(s);
    console.log(s);

    function createChunk(game: Game, pos: Vector2, destroyedOres: string[]): Chunk | undefined {

        if (pos.x < 0 || pos.x > Config.WORLD_WIDTH / 5 || pos.y < 0 || pos.y > Config.WORLD_HEIGHT / 5)
            return;
        
        const ores: (typeof Ore | null)[][] = [[null]];
        __chunkId ++;
    
        for (let y = 0; y < Config.CHUNK_SIZE; y ++) {
            ores.push([]);
            for (let x = 0; x < Config.CHUNK_SIZE; x ++) {
                ores[y].push(null);
                const _y = y + pos.y * 5;
                const _x = x + pos.x * 5;
    
                // const value = (+perlin2(x / 10 + pos.x * 96, y / 10 + pos.y * 96).toFixed(2) + 1) / 1.5;
                
                for (let i = 0; i < generatorSettings.length; i ++) {
                    const gen = generatorSettings[i];
    
                    const div = gen.divider || 10;
                    const value = (+perlin2((x + pos.x * 5) / div, (y + pos.y * 5) / div).toFixed(2) + 1) / 1.5;
    
                    if (!(_x == Math.floor(Config.WORLD_WIDTH / 2) && (_y == 0 || _y == 1 || _y == 2))) {
                        if (_x == 0 || _x == Config.WORLD_WIDTH + 4 || _y == 0 || _y == Config.WORLD_HEIGHT + 4)
                            ores[y][x] = Destrony as any;
                        else
                            if (value >= gen.value[0] && value <= gen.value[1] && _y >= gen.height[0] && _y <= gen.height[1])
                                ores[y][x] = gen.ore;
                    }
                }
                
                const inChunkId = [x, y, pos.x, pos.y].join("-");
                if (ores[y][x] && destroyedOres.indexOf(inChunkId) < 0) {
                    const o = new (ores[y][x] as any)(new Vector2(_x, _y));
                    o.inChunkId = inChunkId;
                    o.group = `chunk-${ __chunkId }`;
                    game.add(o);
                }
    
                
            }
        }
        console.log(`Chunk! ${ __chunkId }`);
    
        // game.add(group);
        game.initChildren(game);
    
        return { id: `chunk-${ __chunkId }`, pos };
    
    }
    
    return {
        createChunk
    }
}
